-- Criação do Funcionário
create table funcionario (
		idFuncionario int primary key auto_increment,
        nome varchar(50),
        cargo varchar(45),
        email varchar(100),
        senha varchar(18),
        telefone char(14),
        cpf char(14),
        dataNascimento date,
        fkSuperior int,
        foreign key (fkSuperior) references funcionario(idFuncionario),
        foto varchar(300)
)auto_increment = 100000;

-- Dados referentes aos usuarios
                           


